DROP DATABASE IF EXISTS `MHGU`;

CREATE DATABASE `MHGU`;

USE `MHGU`;

CREATE TABLE `weapons` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(50),
    info VARCHAR(500)
);

CREATE TABLE weapons_chart (
    ID INT PRIMARY KEY,
    WEAPON_NAME VARCHAR(255),
    WEAPON_POPULARITY INT,
    WEAPON_CLASS VARCHAR(255)
);

CREATE TABLE `hunter arts` (
    id MEDIUMINT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(50),
    info text,
    weapon MEDIUMINT,
    FOREIGN KEY (weapon) REFERENCES `weapons`(id)
);

CREATE TABLE `monsters` (
	id INT NOT NULL AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    type VARCHAR(50) NOT NULL,
    deviant BOOLEAN NOT NULL,
    small_monster BOOLEAN NOT NULL,
    related_monsters VARCHAR(100),
    description text

